   410→    <script>
   411→        let statsVisible = false;
   412→        let autoUpdateInterval = null;
   413→        let hourlyChart = null;
   414→        let dailyChart = null;
   415→
   416→        function toggleStats() {
   417→            const panel = document.getElementById('statsPanel');
   418→            statsVisible = !statsVisible;
   419→            
   420→            if (statsVisible) {
   421→                panel.classList.add('active');
   422→                loadStats();
   423→                // 开启自动更新，每60秒刷新一次
   424→                autoUpdateInterval = setInterval(loadStats, 60000);
   425→            } else {
   426→                panel.classList.remove('active');
   427→                // 关闭自动更新
   428→                if (autoUpdateInterval) {
   429→                    clearInterval(autoUpdateInterval);
   430→                    autoUpdateInterval = null;
   431→                }
   432→            }
   433→        }
   434→
   435→        // 获取最新统计数据
   436→        async function fetchLatestStats() {
   437→            try {
   438→                // 获取日志目录列表
   439→                const response = await fetch('/logs/', {
   440→                    method: 'GET',
   441→                    headers: {'Accept': 'application/json'}
   442→                });
   443→                
   444→                if (!response.ok) throw new Error('无法访问日志目录');
   445→                
   446→                const html = await response.text();
   447→                
   448→                // 解析HTML找到最新的stats文件
   449→                const statsFiles = html.match(/stats_\d{8}_\d{4}\.json/g) || [];
   450→                if (statsFiles.length === 0) throw new Error('未找到统计文件');
   451→                
   452→                // 获取最新文件
   453→                const latestFile = statsFiles.sort().reverse()[0];
   454→                
   455→                // 加载统计数据
   456→                const statsResponse = await fetch(`/logs/${latestFile}`);
   457→                if (!statsResponse.ok) throw new Error('无法加载统计数据');
   458→                
   459→                const data = await statsResponse.json();
   460→                return data;
   461→                
   462→            } catch (error) {
   463→                console.error('获取数据失败:', error);
   464→                throw error;
   465→            }
   466→        }
   467→
   468→        // 更新页面数据
   469→        function updatePage(data) {
   470→            // 更新摘要数据
   471→            document.getElementById('totalVisits24h').textContent = data.summary.total_visits_24h?.toLocaleString() || '--';
   472→            document.getElementById('totalVisits7d').textContent = data.summary.total_visits_7d?.toLocaleString() || '--';
   473→            document.getElementById('todayGrowth').textContent = data.summary.growth_trend || '--';
   474→            document.getElementById('peakHour').textContent = data.summary.peak_hour || '--';
   475→            document.getElementById('peakCount').textContent = data.summary.peak_count || '--';
   476→            document.getElementById('lastUpdate').textContent = data.summary.last_update || '--';
   477→            document.getElementById('avgDaily').textContent = data.summary.avg_daily?.toLocaleString() || '--';
   478→
   479→            // 创建7天趋势图
   480→            if (data.daily_data && data.daily_data.length > 0) {
   481→                createDailyChart(data.daily_data);
   482→            }
   483→
   484→            // 创建24小时图表
   485→            if (data.hourly_data && data.hourly_data.length > 0) {
   486→                createHourlyChart(data.hourly_data);
   487→            }
   488→
   489→            // 显示内容，隐藏加载状态
   490→            document.getElementById('loading').style.display = 'none';
   491→            document.getElementById('error').style.display = 'none';
   492→            document.getElementById('statsContent').style.display = 'block';
   493→        }
   494→
   495→        // 创建7天趋势图
   496→        function createDailyChart(dailyData) {
   497→            const ctx = document.getElementById('dailyChart').getContext('2d');
   498→            
   499→            if (dailyChart) {
   500→                dailyChart.destroy();
   501→            }
   502→            
   503→            const labels = dailyData.map(item => item.label);
   504→            const data = dailyData.map(item => item.count);
   505→            
   506→            dailyChart = new Chart(ctx, {
   507→                type: 'bar',
   508→                data: {
   509→                    labels: labels,
   510→                    datasets: [{
   511→                        label: '每日访问量',
   512→                        data: data,
   513→                        backgroundColor: 'rgba(102, 126, 234, 0.6)',
   514→                        borderColor: '#667eea',
   515→                        borderWidth: 2,
   516→                        borderRadius: 4,
   517→                        borderSkipped: false
   518→                    }]
   519→                },
   520→                options: {
   521→                    responsive: true,
   522→                    maintainAspectRatio: false,
   523→                    plugins: {
   524→                        legend: {
   525→                            display: true,
   526→                            position: 'top',
   527→                            labels: {
   528→                                color: '#333',
   529→                                font: { size: 12 }
   530→                            }
   531→                        }
   532→                    },
   533→                    scales: {
   534→                        y: {
   535→                            beginAtZero: true,
   536→                            grid: { color: 'rgba(0,0,0,0.1)' },
   537→                            ticks: { 
   538→                                color: '#666',
   539→                                callback: function(value) {
   540→                                    return value.toLocaleString();
   541→                                }
   542→                            }
   543→                        },
   544→                        x: {
   545→                            grid: { color: 'rgba(0,0,0,0.1)' },
   546→                            ticks: { color: '#666' }
   547→                        }
   548→                    }
   549→                }
   550→            });
   551→        }
   552→
   553→        // 创建24小时图表
   554→        function createHourlyChart(hourlyData) {
   555→            const ctx = document.getElementById('hourlyChart').getContext('2d');
   556→            
   557→            if (hourlyChart) {
   558→                hourlyChart.destroy();
   559→            }
   560→            
   561→            const labels = hourlyData.map(item => String(item.hour).padStart(2, '0') + ':00');
   562→            const data = hourlyData.map(item => item.count);
   563→            
   564→            hourlyChart = new Chart(ctx, {
   565→                type: 'line',
   566→                data: {
   567→                    labels: labels,
   568→                    datasets: [{
   569→                        label: '每小时访问',
   570→                        data: data,
   571→                        borderColor: '#764ba2',
   572→                        backgroundColor: 'rgba(118, 75, 162, 0.1)',
   573→                        borderWidth: 2,
   574→                        fill: true,
   575→                        tension: 0.4,
   576→                        pointBackgroundColor: '#764ba2',
   577→                        pointBorderColor: '#fff',
   578→                        pointBorderWidth: 2,
   579→                        pointRadius: 4
   580→                    }]
   581→                },
   582→                options: {
   583→                    responsive: true,
   584→                    maintainAspectRatio: false,
   585→                    plugins: {
   586→                        legend: {
   587→                            display: true,
   588→                            position: 'top',
   589→                            labels: {
   590→                                color: '#333',
   591→                                font: { size: 12 }
   592→                            }
   593→                        }
   594→                    },
   595→                    scales: {
   596→                        y: {
   597→                            beginAtZero: true,
   598→                            grid: { color: 'rgba(0,0,0,0.1)' },
   599→                            ticks: { 
   600→                                color: '#666',
   601→                                callback: function(value) {
   602→                                    return value.toLocaleString();
   603→                                }
   604→                            }
   605→                        },
