function displayBlogs(articles) {
    // 定义四个主要分类及其图标
    const categories = {
        'AI技术': { icon: '🤖', articles: [] },
        '架构设计': { icon: '🏗️', articles: [] },
        '广告技术': { icon: '📈', articles: [] },
        '管理技术': { icon: '💼', articles: [] }
    };

    // 按分类分组文章，每类限制4-6篇
    articles.forEach(article => {
        const category = article.category;
        if (categories[category] && categories[category].articles.length < 6) {
            categories[category].articles.push(article);
        }
    });

    // 如果某些分类文章不够，从其他分类补充
    const allCategories = Object.keys(categories);
    allCategories.forEach(catName => {
        if (categories[catName].articles.length < 4) {
            // 从其他分类或未分类文章中补充
            const remainingArticles = articles.filter(article => 
                !Object.values(categories).some(cat => 
                    cat.articles.some(a => a.url === article.url)
                )
            );

            const needed = 4 - categories[catName].articles.length;
            const toAdd = remainingArticles.slice(0, needed);
            categories[catName].articles.push(...toAdd);
        }
    });

    const container = document.getElementById('categoriesContainer');
    container.innerHTML = '';

    Object.entries(categories).forEach(([categoryName, categoryData]) => {
        if (categoryData.articles.length === 0) return;

        const categorySection = document.createElement('div');
        categorySection.className = 'category-section';

        categorySection.innerHTML = `
            <div class="category-title">
                <span class="category-icon">${categoryData.icon}</span>
                <span>${categoryName}</span>
            </div>
            <ul class="blog-list">
                ${categoryData.articles.map(article => `
                    <li class="blog-item">
                        <a href="${article.url}" class="blog-link" target="_blank">
                            <div class="blog-title">${article.title || '技术文章'}</div>
                            <div class="blog-meta">📅 ${article.date || '最近'}</div>
                            <div class="blog-desc">${(article.description || '点击查看详细内容...').substring(0, 100)}...</div>
                        </a>
                    </li>
                `).join('')}
            </ul>
        `;

        container.appendChild(categorySection);
    });

    container.style.display = 'grid';
}

