class HTMLTitleParser(HTMLParser):
    """HTMLæ ‡é¢˜æå–å™¨"""
    def __init__(self):
        super().__init__()
        self.title = ""
        self.in_title = False
        self.meta_description = ""

    def handle_starttag(self, tag, attrs):
