        self.excluded_dirs = {'log', 'webclick', 'pic'}

        # æŠ€æœ¯åˆ†ç±»å…³é”®è¯
        self.categories = {
            'AIæŠ€æœ¯': ['ai', 'claude', 'tts', 'voice', 'machine-learning'],
            'æ¶æ„è®¾è®¡': ['architecture', 'system', 'design', 'dsp', 'adx'],
            'å¹¿å‘ŠæŠ€æœ¯': ['adtech', 'dsp', 'adx', 'rtb'],
            'ç®¡ç†æŠ€æœ¯': ['management', 'paper', 'pdf', 'meituan'],
            'å‰ç«¯æŠ€æœ¯': ['frontend', 'javascript', 'html', 'css'],
            'åç«¯æŠ€æœ¯': ['backend', 'api', 'server', 'database'],
            'äº§å“æŠ€æœ¯': ['product', 'streamlit', 'prfaq'],
            'å…¶ä»–æŠ€æœ¯': ['others', 'tech', 'git']
        }

    def extract_content_from_html(self, file_path):
        """ä»HTMLæ–‡ä»¶æå–æ ‡é¢˜å’Œæè¿°"""
        try:
            with open(file_path, 'r', encoding='utf-8') as f:
                content = f.read()

            parser = HTMLTitleParser()
            parser.feed(content)

            title = parser.title.strip() if parser.title else ""
            description = parser.meta_description.strip()

            # å¦‚æœæ²¡æœ‰metaæè¿°ï¼Œå°è¯•æå–ç¬¬ä¸€æ®µæ–‡æœ¬
            if not description:
                # ç§»é™¤HTMLæ ‡ç­¾ï¼Œè·å–çº¯æ–‡æœ¬
                text = re.sub(r'<[^>]+>', ' ', content)
                text = re.sub(r'\s+', ' ', text).strip()

                # æå–å‰200ä¸ªå­—ç¬¦ä½œä¸ºæè¿°
                if len(text) > 200:
                    description = text[:200] + "..."
                else:
                    description = text if text else "æŠ€æœ¯æ–‡ç« å†…å®¹è¯¦ç»†ä»‹ç»..."

            return title, description

        except Exception as e:
            logging.error(f"è§£ææ–‡ä»¶ {file_path} æ—¶å‡ºé”™: {e}")
            return "", ""

    def determine_category(self, file_path):
        """æ ¹æ®æ–‡ä»¶è·¯å¾„å’Œåç§°ç¡®å®šåˆ†ç±»"""
        path_str = str(file_path).lower()
        filename = file_path.name.lower()

        for category, keywords in self.categories.items():
            for keyword in keywords:
                if keyword in path_str or keyword in filename:
                    return category

        return "æŠ€æœ¯æ–‡ç« "

    def get_file_date(self, file_path):
        """è·å–æ–‡ä»¶ä¿®æ”¹æ—¶é—´"""
        try:
            timestamp = os.path.getmtime(file_path)
            return datetime.fromtimestamp(timestamp).strftime('%Y-%m-%d')
        except:
            return datetime.now().strftime('%Y-%m-%d')

    def is_tech_blog_file(self, file_path):
        """åˆ¤æ–­æ˜¯å¦ä¸ºæŠ€æœ¯åšå®¢æ–‡ä»¶"""
        filename = file_path.name.lower()
        parent_dir = file_path.parent.name.lower()

        # æ’é™¤ç‰¹å®šæ–‡ä»¶
        if filename in self.excluded_files:
            return False

        # æ’é™¤ç‰¹å®šç›®å½•
        if parent_dir in self.excluded_dirs:
            return False

        # åªå¤„ç†HTMLæ–‡ä»¶
        if not filename.endswith('.html'):
            return False

        # æ’é™¤ä»¥ä¸‹æ¨¡å¼çš„æ–‡ä»¶
        exclude_patterns = [
            r'zhanglu.*\.html',  # ä¸ªäººå±•ç¤ºé¡µé¢
            r'.*showcase.*\.html'  # å±•ç¤ºé¡µé¢
        ]

        for pattern in exclude_patterns:
            if re.match(pattern, filename):
                return False

        return True

    def collect_blog_files(self):
        """æœé›†æ‰€æœ‰æŠ€æœ¯åšå®¢æ–‡ä»¶"""
        blog_articles = []

        try:
            for file_path in self.www_dir.rglob("*.html"):
                if not self.is_tech_blog_file(file_path):
                    continue

                logging.info(f"å¤„ç†æ–‡ä»¶: {file_path}")

                # æå–æ–‡ç« ä¿¡æ¯
                title, description = self.extract_content_from_html(file_path)
                category = self.determine_category(file_path)
                date = self.get_file_date(file_path)

                # ç”Ÿæˆç›¸å¯¹URL
                relative_path = file_path.relative_to(self.www_dir)
                url = f"/{relative_path}"

                article = {
                    "title": title or file_path.stem.replace('-', ' ').title(),
                    "description": description,
                    "category": category,
                    "date": date,
                    "url": url,
                    "file_path": str(file_path)
                }

                blog_articles.append(article)

        except Exception as e:
            logging.error(f"æœé›†åšå®¢æ–‡ä»¶æ—¶å‡ºé”™: {e}")

        return blog_articles

    def generate_blog_data(self):
        """ç”Ÿæˆåšå®¢æ•°æ®JSONæ–‡ä»¶"""
        logging.info("å¼€å§‹æœé›†æŠ€æœ¯åšå®¢æ–‡ç« ...")

        articles = self.collect_blog_files()

        # æŒ‰æ—¥æœŸæ’åºï¼ˆæœ€æ–°çš„åœ¨å‰ï¼‰
        articles.sort(key=lambda x: x['date'], reverse=True)

        blog_data = {
