    81â†’                const statsResponse = await fetch(`/logs/${latestFile}`);
    82â†’                if (!statsResponse.ok) throw new Error('æ— æ³•åŠ è½½ç»Ÿè®¡æ•°æ®');
    83â†’                
    84â†’                const data = await statsResponse.json();
    85â†’                return data;
    86â†’                
    87â†’            } catch (error) {
    88â†’                console.error('è·å–æ•°æ®å¤±è´¥:', error);
    89â†’                throw error;
