    91â†’        }
    92â†’
    93â†’        // æ›´æ–°é¡µé¢æ•°æ®
    94â†’        function updatePage(data) {
    95â†’            // æ›´æ–°æ‘˜è¦æ•°æ®
    96â†’            document.getElementById('totalVisits').textContent = data.summary.total_visits;
    97â†’            document.getElementById('peakHour').textContent = data.summary.peak_hour;
    98â†’            document.getElementById('avgHourly').textContent = data.summary.avg_hourly;
    99â†’            document.getElementById('lastUpdate').textContent = data.summary.last_update;
   100â†’
   101â†’            // å‡†å¤‡å›¾è¡¨æ•°æ®
   102â†’            const hourlyData = data.hourly_data.map(item => item.count);
