# HTTPåˆ°HTTPSé‡å®šå‘
server {
    listen 80;
    server_name linapp.fun www.linapp.fun;
    return 301 https://$server_name$request_uri;
}

# HTTPSæœåŠ¡å™¨é…ç½®
server {
    listen 443 ssl;
    server_name linapp.fun www.linapp.fun;
    ssl_certificate /etc/letsencrypt/live/linapp.fun/fullchain.pem;
    ssl_certificate_key /etc/letsencrypt/live/linapp.fun/privkey.pem;

    # SSLå®‰å…¨é…ç½®
    include /etc/letsencrypt/options-ssl-nginx.conf;
    ssl_dhparam /etc/letsencrypt/ssl-dhparams.pem;

    # å±è”½æ¶æ„User-Agent
    if ($http_user_agent ~* (wget|curl|libwww|python|nikto|scan|java|winhttp|clshttp|loader)) {
        return 444;
    }

    # å±è”½SQLæ³¨å…¥å°è¯•
    if ($args ~* (union|select|insert|delete|update|concat|database|script|javascript|vbscript)) {
        return 444;
    }

    # é€šç”¨é¢‘ç‡é™åˆ¶
    limit_req zone=general burst=20 nodelay;

    # ä¸»é¡µå¯¼èˆª
    location / {
        root /mnt/www;
        index index.html;
        try_files $uri $uri/ =404;

        # æ·»åŠ å®‰å…¨å¤´
        add_header X-Frame-Options DENY;
        add_header X-Content-Type-Options nosniff;
        add_header X-XSS-Protection "1; mode=block";
        add_header Referrer-Policy strict-origin-when-cross-origin;
    }

    # è®¿é—®ç»Ÿè®¡æ•°æ®API
    location /logs/ {
        alias /home/main/logs/;
        autoindex on;
        autoindex_format json;

        # ä»…å…è®¸JSONæ–‡ä»¶è®¿é—®
        location ~ \.json$ {
            add_header Content-Type application/json;
            add_header Access-Control-Allow-Origin *;
            add_header Cache-Control "no-cache, no-store, must-revalidate";
        }

        # å±è”½å…¶ä»–æ–‡ä»¶ç±»å‹
        location ~ \.(log|txt|sh)$ {
            return 404;
        }
    }

    # PHPå¤„ç† - ä»…å…è®¸api.php
    location ~ ^/api\.php$ {
        root /mnt/www;
        fastcgi_pass unix:/var/run/php/php7.4-fpm.sock;
        fastcgi_index index.php;
        fastcgi_param SCRIPT_FILENAME $document_root$fastcgi_script_name;
        include fastcgi_params;

        # APIç‰¹æ®Šé¢‘ç‡é™åˆ¶
        limit_req zone=api burst=10 nodelay;
    }

    # å±è”½å…¶ä»–PHPæ–‡ä»¶
    location ~ \.php$ {
        return 444;
    }

    # å±è”½æ¶æ„æ‰«æè·¯å¾„
    location ~ /\.git {
        access_log /var/log/nginx/security.log;
        return 444;
    }

    location ~ ^/(wp-|wordpress/|wp-admin|wp-content|wp-includes) {
        access_log /var/log/nginx/security.log;
        return 444;
    }

    location ~ /\.(env|config) {
        access_log /var/log/nginx/security.log;
        return 444;
    }

    location ~ ^/(phpmyadmin|pma|adminer|phpMyAdmin) {
        access_log /var/log/nginx/security.log;
        return 444;
    }

    location ~ ^/(admin|administrator|manage|control|cpanel) {
        access_log /var/log/nginx/security.log;
        return 444;
    }

    location ~ ^/(xmlrpc\.php|readme\.html|license\.txt) {
        access_log /var/log/nginx/security.log;
        return 444;
    }

    location ~ /\.(sql|bak|backup|old|orig|save)$ {
        access_log /var/log/nginx/security.log;
        return 444;
    }

    location ~ ^/(vendor|composer|node_modules|\.svn|\.hg|\.bzr) {
        access_log /var/log/nginx/security.log;
        return 444;
    }

    # Streamlitåº”ç”¨è·¯å¾„
    location /app {
        proxy_pass http://127.0.0.1:8501;
        proxy_set_header Host $host;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header X-Forwarded-Proto $scheme;

        # WebSocket support for Streamlit
        proxy_http_version 1.1;
        proxy_set_header Upgrade $http_upgrade;
        proxy_set_header Connection "upgrade";
        proxy_read_timeout 86400;
    }
}

