   303→        // 获取最新统计数据
   304→        async function fetchLatestStats() {
   305→            try {
   306→                // 获取日志目录列表
   307→                const response = await fetch('/logs/', {
   308→                    method: 'GET',
   309→                    headers: {'Accept': 'application/json'}
   310→                });
   311→                
   312→                if (!response.ok) throw new Error('无法访问日志目录');
   313→                
   314→                const html = await response.text();
   315→                
   316→                // 解析HTML找到最新的stats文件
   317→                const statsFiles = html.match(/stats_\d{8}_\d{4}\.json/g) || [];
   318→                if (statsFiles.length === 0) throw new Error('未找到统计文件');
   319→                
   320→                // 获取最新文件
   321→                const latestFile = statsFiles.sort().reverse()[0];
   322→                
   323→                // 加载统计数据
   324→                const statsResponse = await fetch(`/logs/${latestFile}`);
   325→                if (!statsResponse.ok) throw new Error('无法加载统计数据');
   326→                
   327→                const data = await statsResponse.json();
   328→                return data;
   329→                
   330→            } catch (error) {
   331→                console.error('获取数据失败:', error);
   332→                throw error;
   333→            }
   334→        }
   335→
   336→        // 更新页面数据
   337→        function updatePage(data) {
   338→            // 更新摘要数据
   339→            document.getElementById('totalVisits24h').textContent = data.summary.total_visits_24h?.toLocaleString() || '--';
   340→            document.getElementById('totalVisits7d').textContent = data.summary.total_visits_7d?.toLocaleString() || '--';
   341→            document.getElementById('todayGrowth').textContent = data.summary.growth_trend || '--';
   342→            document.getElementById('peakHour').textContent = data.summary.peak_hour || '--';
   343→            document.getElementById('peakCount').textContent = data.summary.peak_count || '--';
   344→            document.getElementById('lastUpdate').textContent = data.summary.last_update || '--';
   345→            document.getElementById('avgDaily').textContent = data.summary.avg_daily?.toLocaleString() || '--';
   346→
   347→            // 创建7天趋势图
   348→            if (data.daily_data && data.daily_data.length > 0) {
   349→                createDailyChart(data.daily_data);
   350→            }
   351→
   352→            // 创建24小时图表
   353→            if (data.hourly_data && data.hourly_data.length > 0) {
   354→                createHourlyChart(data.hourly_data);
   355→            }
   356→
   357→            // 显示内容，隐藏加载状态
   358→            document.getElementById('loading').style.display = 'none';
   359→            document.getElementById('error').style.display = 'none';
   360→            document.getElementById('statsContent').style.display = 'block';
   361→        }
   362→
   363→        // 创建7天趋势图
   364→        function createDailyChart(dailyData) {
   365→            const ctx = document.getElementById('dailyChart').getContext('2d');
   366→            
   367→            if (dailyChart) {
   368→                dailyChart.destroy();
   369→            }
   370→            
   371→            const labels = dailyData.map(item => item.label);
   372→            const data = dailyData.map(item => item.count);
   373→            
   374→            dailyChart = new Chart(ctx, {
   375→                type: 'bar',
   376→                data: {
   377→                    labels: labels,
   378→                    datasets: [{
   379→                        label: '每日访问量',
   380→                        data: data,
   381→                        backgroundColor: 'rgba(102, 126, 234, 0.6)',
   382→                        borderColor: '#667eea',
   383→                        borderWidth: 2,
   384→                        borderRadius: 4,
   385→                        borderSkipped: false
   386→                    }]
   387→                },
   388→                options: {
   389→                    responsive: true,
   390→                    maintainAspectRatio: false,
   391→                    plugins: {
   392→                        legend: {
   393→                            display: true,
   394→                            position: 'top',
   395→                            labels: {
   396→                                color: '#333',
   397→                                font: { size: 12 }
   398→                            }
   399→                        }
   400→                    },
   401→                    scales: {
   402→                        y: {
   403→                            beginAtZero: true,
   404→                            grid: { color: 'rgba(0,0,0,0.1)' },
   405→                            ticks: { 
   406→                                color: '#666',
   407→                                callback: function(value) {
   408→                                    return value.toLocaleString();
   409→                                }
   410→                            }
   411→                        },
   412→                        x: {
   413→                            grid: { color: 'rgba(0,0,0,0.1)' },
   414→                            ticks: { color: '#666' }
   415→                        }
   416→                    }
   417→                }
   418→            });
   419→        }
