   422→        function createHourlyChart(hourlyData) {
   423→            const ctx = document.getElementById('hourlyChart').getContext('2d');
   424→            
   425→            if (hourlyChart) {
   426→                hourlyChart.destroy();
   427→            }
   428→            
   429→            const labels = hourlyData.map(item => String(item.hour).padStart(2, '0') + ':00');
   430→            const data = hourlyData.map(item => item.count);
   431→            
   432→            hourlyChart = new Chart(ctx, {
   433→                type: 'line',
   434→                data: {
   435→                    labels: labels,
   436→                    datasets: [{
