   484→        // 加载数据
   485→        async function loadStats() {
   486→            try {
   487→                // 显示加载状态
   488→                document.getElementById('loading').style.display = 'block';
   489→                document.getElementById('error').style.display = 'none';
   490→                document.getElementById('statsContent').style.display = 'none';
   491→                
   492→                const data = await fetchLatestStats();
   493→                updatePage(data);
   494→            } catch (error) {
   495→                document.getElementById('loading').style.display = 'none';
   496→                document.getElementById('error').style.display = 'block';
   497→                document.getElementById('statsContent').style.display = 'none';
   498→                console.error('加载失败:', error);
   499→            }
   500→        }
   501→
   502→        // 页面加载完成后的初始化
   503→        document.addEventListener('DOMContentLoaded', function() {
   504→            loadStats();
   505→            
   506→            // 开启自动更新，每60秒刷新一次
   507→            autoUpdateInterval = setInterval(loadStats, 60000);
   508→        });
   509→
   510→        // 页面卸载时清理定时器
   511→        window.addEventListener('beforeunload', function() {
   512→            if (autoUpdateInterval) {
   513→                clearInterval(autoUpdateInterval);
   514→                autoUpdateInterval = null;
   515→            }
   516→        });
   517→    </script>
   518→</body>
