server {
    if ($host = pr.linapp.fun) {
        return 301 https://$host$request_uri;
    } # managed by Certbot


    listen 80;
    server_name pr.linapp.fun;

    # é‡å®šå‘æ‰€æœ‰HTTPæµé‡åˆ°HTTPS
    return 301 https://$server_name$request_uri;


}

server {
    listen 443 ssl http2;
    server_name pr.linapp.fun;

    # SSLè¯ä¹¦é…ç½®ï¼ˆå¾…Certbotè‡ªåŠ¨æ·»åŠ ï¼‰
    # ssl_certificate /etc/letsencrypt/live/pr.linapp.fun/fullchain.pem;
    # ssl_certificate_key /etc/letsencrypt/live/pr.linapp.fun/privkey.pem;

    # SSLå®‰å…¨é…ç½®
    ssl_protocols TLSv1.2 TLSv1.3;
    ssl_ciphers ECDHE-RSA-AES128-GCM-SHA256:ECDHE-RSA-AES256-GCM-SHA384:ECDHE-RSA-AES128-SHA256:ECDHE-RSA-AES256-SHA384;
    ssl_prefer_server_ciphers off;
    ssl_session_cache shared:SSL:10m;
    ssl_session_timeout 5m;

    # å®‰å…¨å¤´éƒ¨
    add_header Strict-Transport-Security "max-age=31536000; includeSubDomains" always;
    add_header X-Content-Type-Options nosniff;
    add_header X-Frame-Options DENY;
    add_header X-XSS-Protection "1; mode=block";

    # ä»£ç†é…ç½®
    location / {
        proxy_pass http://127.0.0.1:8501;
        proxy_set_header Host $host;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header X-Forwarded-Proto $scheme;
        proxy_set_header X-Forwarded-Port $server_port;

        # WebSocketæ”¯æŒ (Streamlitéœ€è¦)
