# äº‘æœåŠ¡åŒºåŸŸå¯è§†åŒ–ç³»ç»Ÿ - az.linapp.fun (HTTPç‰ˆæœ¬)
server {
    server_name az.linapp.fun www.az.linapp.fun;

    # ç½‘ç«™æ ¹ç›®å½•
    root /home/az/cloud-az-visualizer;
    index index.html;

    # æ—¥å¿—é…ç½®
    access_log /var/log/nginx/az.linapp.fun.access.log;
    error_log /var/log/nginx/az.linapp.fun.error.log;

    # ä¸»åº”ç”¨ä»£ç†åˆ°Flask
    location / {
        proxy_pass http://127.0.0.1:5001;
        proxy_set_header Host $host;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header X-Forwarded-Proto $scheme;

        # WebSocket æ”¯æŒ (æœªæ¥æ‰©å±•)
        proxy_http_version 1.1;
        proxy_set_header Upgrade $http_upgrade;
        proxy_set_header Connection "upgrade";

        # è¶…æ—¶é…ç½®
        proxy_connect_timeout 60s;
        proxy_send_timeout 60s;
        proxy_read_timeout 60s;
    }

    # é™æ€æ–‡ä»¶ç›´æ¥æœåŠ¡ (æ€§èƒ½ä¼˜åŒ–)
    location /static/ {
        proxy_pass http://127.0.0.1:5001;
        expires 1y;
        add_header Cache-Control "public, immutable";

        # å‹ç¼©é™æ€æ–‡ä»¶
        gzip on;
        gzip_vary on;
        gzip_min_length 1024;
        gzip_types text/css application/javascript application/json image/svg+xml;
    }

    # APIè·¯ç”±ä¼˜åŒ–
    location /api/ {
        proxy_pass http://127.0.0.1:5001;
        proxy_set_header Host $host;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header X-Forwarded-Proto $scheme;

        # APIç¼“å­˜é…ç½®
        proxy_cache_bypass $http_upgrade;
        add_header X-Cache-Status $upstream_cache_status;
    }

    # å¥åº·æ£€æŸ¥ç«¯ç‚¹
    location /health {
        proxy_pass http://127.0.0.1:5001/health;
        access_log off;
    }

    # å®‰å…¨é…ç½® - éšè—æ•æ„Ÿæ–‡ä»¶
    location ~ /\. {
        deny all;
    }

    location ~ \.(env|py|db)$ {
        deny all;
    }

    # é”™è¯¯é¡µé¢
    error_page 404 /404.html;
    error_page 500 502 503 504 /50x.html;

    listen 443 ssl; # managed by Certbot
    ssl_certificate /etc/letsencrypt/live/az.linapp.fun/fullchain.pem; # managed by Certbot
    ssl_certificate_key /etc/letsencrypt/live/az.linapp.fun/privkey.pem; # managed by Certbot
    include /etc/letsencrypt/options-ssl-nginx.conf; # managed by Certbot
    ssl_dhparam /etc/letsencrypt/ssl-dhparams.pem; # managed by Certbot


}
server {
    if ($host = www.az.linapp.fun) {
        return 301 https://$host$request_uri;
    } # managed by Certbot


    if ($host = az.linapp.fun) {
        return 301 https://$host$request_uri;
    } # managed by Certbot


    listen 80;
    server_name az.linapp.fun www.az.linapp.fun;
    return 404; # managed by Certbot




}

