# HTTPæœåŠ¡å™¨ - é‡å®šå‘åˆ°HTTPS
server {
    listen 80;
    listen [::]:80;
    server_name 6page.linapp.fun;

    # Let's EncryptéªŒè¯
    location /.well-known/acme-challenge/ {
        root /var/www/6page.linapp.fun/public;
        allow all;
    }

    # é‡å®šå‘åˆ°HTTPS
    location / {
        return 301 https://$server_name$request_uri;
    }
}

# HTTPSæœåŠ¡å™¨
server {
    listen 443 ssl http2;
    listen [::]:443 ssl http2;
    server_name 6page.linapp.fun;

    root /var/www/6page.linapp.fun/public;
    index index.php index.html index.htm;

    # SSLè¯ä¹¦é…ç½®
    ssl_certificate /etc/letsencrypt/live/6page.linapp.fun/fullchain.pem;
    ssl_certificate_key /etc/letsencrypt/live/6page.linapp.fun/privkey.pem;
    ssl_protocols TLSv1.2 TLSv1.3;
    ssl_ciphers ECDHE-RSA-AES128-GCM-SHA256:ECDHE-RSA-AES256-GCM-SHA384;
    ssl_prefer_server_ciphers off;

    # å®‰å…¨å¤´
    add_header X-Frame-Options "SAMEORIGIN" always;
    add_header X-XSS-Protection "1; mode=block" always;
    add_header X-Content-Type-Options "nosniff" always;
    add_header Referrer-Policy "no-referrer-when-downgrade" always;
    add_header Content-Security-Policy "default-src 'self' http: https: data: blob: 'unsafe-inline'" always;

    # PHPå¤„ç† - ä¿®å¤ç‰ˆæœ¬
    location ~ \.php$ {
        try_files $uri =404;
        fastcgi_split_path_info ^(.+\.php)(/.+)$;
        fastcgi_pass unix:/var/run/php/php7.4-fpm.sock;
        fastcgi_index index.php;
        fastcgi_param SCRIPT_FILENAME $document_root$fastcgi_script_name;
        fastcgi_param PATH_INFO $fastcgi_path_info;
        include fastcgi_params;
        fastcgi_read_timeout 300;
    }

    # ThinkPHP URLé‡å†™
    location / {
        try_files $uri $uri/ /index.php$is_args$args;
    }

    # ç¦æ­¢è®¿é—®æ•æ„Ÿæ–‡ä»¶
    location ~ ^/(\.user\.ini|\.htaccess|\.git|\.env|\.svn|LICENSE|README\.md)$ {
        return 404;
    }

    # é™æ€æ–‡ä»¶ç¼“å­˜
    location ~* \.(jpg|jpeg|png|gif|ico|css|js)$ {
        expires 1y;
        add_header Cache-Control "public, immutable";
    }
}

