server {
    server_name meet.linapp.fun;

    location /static/ {
        alias /home/meet/meeting-room-booking/static/;
        expires 30d;
    }

    location / {
        proxy_pass http://127.0.0.1:8000;
        proxy_set_header Host $host;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
    }

    listen 443 ssl; # managed by Certbot
    ssl_certificate /etc/letsencrypt/live/meet.linapp.fun/fullchain.pem; # managed by Certbot
    ssl_certificate_key /etc/letsencrypt/live/meet.linapp.fun/privkey.pem; # managed by Certbot
    include /etc/letsencrypt/options-ssl-nginx.conf; # managed by Certbot
    ssl_dhparam /etc/letsencrypt/ssl-dhparams.pem; # managed by Certbot

}
server {
    if ($host = meet.linapp.fun) {
        return 301 https://$host$request_uri;
    } # managed by Certbot


    listen 80;
    server_name meet.linapp.fun;
    return 404; # managed by Certbot


}

