    22→<body>
    23→    <div class="container">
    24→        <h1>📊 主页访问统计 <span id="autoRefresh">🔄</span></h1>
    25→        
    26→        <div class="stats-summary">
    27→            <div class="stat-box">
    28→                <div class="stat-number" id="totalVisits">--</div>
    29→                <div class="stat-label">24小时总访问</div>
    30→            </div>
    31→            <div class="stat-box">
    32→                <div class="stat-number" id="peakHour">--:--</div>
    33→                <div class="stat-label">访问高峰</div>
    34→            </div>
    35→            <div class="stat-box">
    36→                <div class="stat-number" id="avgHourly">--</div>
    37→                <div class="stat-label">平均每小时</div>
    38→            </div>
    39→        </div>
    40→
    41→        <div id="loading" class="loading">正在加载数据...</div>
    42→        <div id="error" class="error" style="display:none;">数据加载失败，正在重试...</div>
    43→
    44→        <div id="chartContainer" style="display:none;">
    45→            <canvas id="visitChart"></canvas>
    46→        </div>
    47→
    48→        <div class="update-time">
    49→            最后更新: <span id="lastUpdate">--</span> | 
    50→            自动刷新: <span id="nextUpdate">--</span>秒后
    51→        </div>
    52→    </div>
    53→
    54→    <script>
    55→        let chart;
    56→        let refreshTimer;
    57→        let countdownTimer;
    58→        let nextRefresh = 60;
    59→
    60→        // 获取最新统计数据
    61→        async function fetchLatestStats() {
    62→            try {
    63→                // 获取日志目录列表
    64→                const response = await fetch('/log/', {
    65→                    method: 'GET',
    66→                    headers: {'Accept': 'text/html'}
    67→                });
    68→                
    69→                if (!response.ok) throw new Error('无法访问日志目录');
    70→                
    71→                const html = await response.text();
    72→                
    73→                // 解析HTML找到最新的stats文件
    74→                const statsFiles = html.match(/stats_\d{8}_\d{4}\.json/g) || [];
    75→                if (statsFiles.length === 0) throw new Error('未找到统计文件');
    76→                
    77→                // 获取最新文件
    78→                const latestFile = statsFiles.sort().reverse()[0];
    79→                
    80→                // 加载统计数据
    81→                const statsResponse = await fetch(`/log/${latestFile}`);
    82→                if (!statsResponse.ok) throw new Error('无法加载统计数据');
    83→                
    84→                const data = await statsResponse.json();
    85→                return data;
    86→                
    87→            } catch (error) {
    88→                console.error('获取数据失败:', error);
    89→                throw error;
    90→            }
    91→        }
    92→
    93→        // 更新页面数据
    94→        function updatePage(data) {
    95→            // 更新摘要数据
    96→            document.getElementById('totalVisits').textContent = data.summary.total_visits;
    97→            document.getElementById('peakHour').textContent = data.summary.peak_hour;
    98→            document.getElementById('avgHourly').textContent = data.summary.avg_hourly;
    99→            document.getElementById('lastUpdate').textContent = data.summary.last_update;
   100→
   101→            // 准备图表数据
   102→            const hourlyData = data.hourly_data.map(item => item.count);
   103→            const hours = data.hourly_data.map(item => String(item.hour).padStart(2, '0') + ':00');
   104→
   105→            // 更新或创建图表
   106→            if (chart) {
   107→                chart.data.datasets[0].data = hourlyData;
   108→                chart.update();
   109→            } else {
   110→                const ctx = document.getElementById('visitChart').getContext('2d');
   111→                chart = new Chart(ctx, {
   112→                    type: 'line',
   113→                    data: {
   114→                        labels: hours,
   115→                        datasets: [{
   116→                            label: '访问次数',
   117→                            data: hourlyData,
   118→                            borderColor: '#007bff',
   119→                            backgroundColor: 'rgba(0, 123, 255, 0.1)',
   120→                            borderWidth: 3,
   121→                            fill: true,
   122→                            tension: 0.4,
   123→                            pointBackgroundColor: '#007bff',
   124→                            pointBorderColor: '#fff',
   125→                            pointBorderWidth: 2,
   126→                            pointRadius: 5
   127→                        }]
   128→                    },
   129→                    options: {
   130→                        responsive: true,
   131→                        maintainAspectRatio: false,
   132→                        plugins: { legend: { display: false } },
   133→                        scales: {
   134→                            y: { beginAtZero: true, grid: { color: 'rgba(0,0,0,0.1)' } },
   135→                            x: { grid: { color: 'rgba(0,0,0,0.1)' } }
   136→                        },
   137→                        interaction: { intersect: false, mode: 'index' }
   138→                    }
   139→                });
   140→            }
   141→
   142→            // 显示图表，隐藏加载状态
   143→            document.getElementById('loading').style.display = 'none';
   144→            document.getElementById('error').style.display = 'none';
   145→            document.getElementById('chartContainer').style.display = 'block';
   146→        }
   147→
   148→        // 加载数据
   149→        async function loadData() {
   150→            try {
   151→                const data = await fetchLatestStats();
   152→                updatePage(data);
   153→            } catch (error) {
   154→                document.getElementById('loading').style.display = 'none';
   155→                document.getElementById('error').style.display = 'block';
   156→                console.error('加载失败:', error);
   157→            }
   158→        }
   159→
   160→        // 倒计时显示
   161→        function updateCountdown() {
   162→            document.getElementById('nextUpdate').textContent = nextRefresh;
   163→            nextRefresh--;
   164→            
   165→            if (nextRefresh < 0) {
   166→                nextRefresh = 60;
   167→                loadData();
   168→            }
   169→        }
   170→
   171→        // 初始化
   172→        document.addEventListener('DOMContentLoaded', function() {
   173→            loadData();
   174→            
   175→            // 每60秒自动刷新数据
   176→            refreshTimer = setInterval(loadData, 60000);
   177→            
   178→            // 每秒更新倒计时
   179→            countdownTimer = setInterval(updateCountdown, 1000);
   180→        });
   181→
   182→        // 页面卸载时清理定时器
   183→        window.addEventListener('beforeunload', function() {
   184→            if (refreshTimer) clearInterval(refreshTimer);
   185→            if (countdownTimer) clearInterval(countdownTimer);
   186→        });
   187→    </script>
   188→</body>
   189→</html>

